import React, { useState, useEffect } from 'react';

const FormularioServico = ({ adicionarServico, servicoAtual, atualizarServico }) => {
  const [nome, setNome] = useState('');
  const [descricao, setDescricao] = useState('');

  useEffect(() => {
    if (servicoAtual) {
      setNome(servicoAtual.nome);
      setDescricao(servicoAtual.descricao);
    } else {
      setNome('');
      setDescricao('');
    }
  }, [servicoAtual]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (nome && descricao) {
      if (servicoAtual) {
        atualizarServico({ nome, descricao });
      } else {
        adicionarServico({ nome, descricao });
      }
      setNome('');
      setDescricao('');
    }
  };

  return (
    <form className="formulario" onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Nome do Serviço"
        value={nome}
        onChange={(e) => setNome(e.target.value)}
      />
      <input
        type="text"
        placeholder="Descrição do Serviço"
        value={descricao}
        onChange={(e) => setDescricao(e.target.value)}
      />
      <button type="submit">{servicoAtual ? 'Atualizar Serviço' : 'Adicionar Serviço'}</button>
    </form>
  );
};

export default FormularioServico;
