import React, { useState } from 'react';
import ServiceForm from './components/cadastroServico';
import ServiceList from './components/listaServico';

const App = () => {
  const [services, setServices] = useState([]);
  const [currentService, setCurrentService] = useState(null);

  const addService = (service) => {
    setServices([...services, service]);
  };

  const editService = (service) => {
    setCurrentService(service);
  };

  const updateService = (updatedService) => {
    setServices(
      services.map((service) =>
        service.name === updatedService.name ? updatedService : service
      )
    );
    setCurrentService(null);
  };

  const deleteService = (serviceToDelete) => {
    setServices(services.filter((service) => service !== serviceToDelete));
  };

  return (
    <div>
      <h1>Gerenciador de Servi√ßos</h1>
      <ServiceForm addService={addService} />
      <ServiceList
        services={services}
        editService={editService}
        deleteService={deleteService}
      />
      {currentService && (
        <ServiceForm
          addService={updateService}
          service={currentService}
        />
      )}
    </div>
  );
};

export default App;
